import useInput from "../hooks/use-input";
const BasicForm = (props) => {
  const {
    value: enteredFirstName,
    isValid: enteredFirstNameIsValid,
    hasError: enteredFirstNameHasError,
    enteredFirstNameChangeHandler,
    enteredFirstNameBlurHandler,
    resetenteredFirstName,
  } = useInput((value) => value.trim() !== "");

  let formIsValid = false;

  if (enteredFirstNameIsValid) {
    formIsValid = true;
  }

  const firstNameClasses = enteredFirstNameIsValid
    ? "form-control"
    : "form-control Invalid";

  return (
    <form>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredFirstName}
            onChange={enteredFirstNameChangeHandler}
            onBlur={enteredFirstNameBlurHandler}
          />
          {enteredFirstNameHasError && (
            <p className="error-text">First Name cannot be Empty</p>
          )}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
