import useInput from "../hooks/use-input";
const BasicForm = (props) => {
  const {
    value: enteredFirstName,
    isValid: enteredFirstNameIsValid,
    hasError: enteredFirstNameHasError,
    enteredFirstNameChangeHandler,
    enteredFirstNameBlurHandler,
    resetenteredFirstName,
  } = useInput((value) => value.trim() !== "");

  const {
    value: enteredLastName,
    isValid: enteredLastNameIsValid,
    hasError: enteredLastNameHasError,
    enteredLastNameChangeHandler,
    enteredLastNameBlurHandler,
    resetenteredLastName,
  } = useInput((value) => value.trim() !== "");

  const {
    value: enteredEmail,
    isValid: enteredEmailIsValid,
    hasError: enteredEmailHasError,
    enteredEmailChangeHandler,
    enteredEmailBlurHandler,
    resetenteredEmail,
  } = useInput((value) => value.includes("@"));

  let formIsValid = false;

  if (enteredFirstNameIsValid && enteredLastNameIsValid) {
    formIsValid = true;
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (!enteredFirstNameIsValid) {
      return;
    }

    if (!enteredLastNameIsValid) {
      return;
    }

    resetenteredFirstName();
    resetenteredLastName();
  };

  const firstNameClasses = enteredFirstNameHasError
    ? "form-control"
    : "form-control Invalid";

  const lastNameClasses = enteredLastNameHasError
    ? "form-control"
    : "form-control Invalid";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="control-group">
        <div className={firstNameClasses}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredFirstName}
            onChange={enteredLastNameChangeHandler}
            onBlur={enteredLastNameBlurHandler}
          />
          {enteredLastNameHasError && (
            <p className="error-text">Last Name cannot be Empty</p>
          )}
        </div>
      </div>
      <div className="control-group">
        <div className={lastNameClasses}>
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            value={enteredLastName}
            onChange={enteredFirstNameChangeHandler}
            onBlur={enteredFirstNameBlurHandler}
          />
          {enteredFirstNameHasError && (
            <p className="error-text">First Name cannot be Empty</p>
          )}
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
